var React = require("react")

String.prototype.capitalize = function() {
    return this.charAt(0).toUpperCase() + this.slice(1);
}


   var DataTable = (props) => {
    return (
      <table className ="table">
        <thead id  ="headers">
          <tr>
            {
             props.cols !== undefined ? props.cols.map((d, i) =>
              <th key = {"header" + i}> {d.capitalize()} </th>
              ) : null
           }
          </tr>
        </thead>
        <tbody>
          {
            props.rows !== undefined ? props.rows.map((row, i) =>
              <tr key = {"row"+i}>
              {
               props.rows !== undefined ?  props.cols.map((col, i) =>
                  <td key = {col} style = {{background: col === "points" ? props.colors(row[col]) : "white"}}> <span> { row[col] } </span> </td>
                  ) : null
              }
              </tr>
              ) : null
          }
        </tbody>

      </table>
      )
   }

   export default DataTable
