var React = require("react")
var d3 = require("d3")
const _ = require('lodash');

class Table extends React.Component {
  render() {

  if (this.props.columns !== undefined) {
    const tableColumns = this.props.columns
    const tableRows = this.props.data

   const tableColumnsFiltered = tableColumns.filter(d => (d === "date") || (d === "endorser") || (d === "position") || (d === "points"))

  tableRows.forEach(function(v){
    delete v.city
    delete v.category
    delete v.body

  });



    console.log(tableRows)

     var headers = (
       <thead>
         <tr>
           {
            tableColumnsFiltered.map((d, i) =>
             <th key = {"header" + i}> {d} </th>
             )
          }
         </tr>
       </thead>
       )

     var rows = tableRows.map((d) =>
         <tr>
          {
            tableColumns.map((col, i) =>
              <td key = {"rows" +i}> {d[col]} </td>
              )
          }
        </tr>
     )


  }


    return (
      <table className = "table">
      { headers }
      { rows }
      </table>
      )
  }
}

export default Table
