var React = require("react")
var d3 = require("d3")
const _ = require('lodash');

class Table extends React.Component {
  render() {

  if (this.props.columns !== undefined) {
    const tableColumns = this.props.columns
    const tableRows = this.props.data

   // const tableColumnsFiltered = tableColumns.filter(d => (d === "date") || (d === "endorser") || (d === "position") || (d === "points"))
   const tableColumnsFiltered = ["date", "endorser", "position", "points"]
   let tableRowsFiltered = _.map(tableRows, d => {
         return _.omit(d, ['city', 'category', "body", "district", "endorsee", "endorser party", "order", "source", "state"])
       })

   console.log(tableRowsFiltered)
   console.log(tableColumnsFiltered)

   const dataTable = () => {
    return (
      <table className ="table">
        <thead id  ="headers">
          <tr>
            {
             tableColumnsFiltered.map((d, i) =>
              <th key = {"header" + i}> {d} </th>
              )
           }
          </tr>
        </thead>
        <tbody>
          {
            tableRowsFiltered.map(row =>
              <tr key = {row.id}>
              {
                tableColumnsFiltered.map(col =>
                  <td key = {col.endorser}> row[col] </td>
                  )
              }

              </tr>
              )
          }
        </tbody>

      </table>
      )
   }

     var headers = (
       <thead id  ="headers">
         <tr>
           {
            tableColumnsFiltered.map((d, i) =>
             <th key = {"header" + i}> {d} </th>
             )
          }
         </tr>
       </thead>
       )

     var rows = tableRowsFiltered.map((d, i) =>
         <tr key = {"rows" +i}>
          {
            tableColumns.map((col, index) =>
              <td key = {"values" +index}> {d[col]} </td>
              )
          }
        </tr>
     )


  }


    return (
     {dataTable}
      )
  }
}

export default Table
