      //       .domain([0, d3.max(totals_array)])
      //       .range([0, 71]);

    }

    return (
      <table className ="table">
            <tbody>
              {data.map((rows, i) => (
                <tr>
                  <td>{endorsees[i]}</td>
                  <td>
                    <svg>
                      <g style = {{
                          fill: colors(i)
                        }}>
                        {rows.map((d, j) => (
                          <rect
                            height={20}
                            width={d[1] - d[0]}
                            x={d[0]}
                          />
                        ))}
                      </g>
                    </svg>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
      )
  }
}

export default TableChart
