import './App.css';
import data from './endorsements-2020.csv';
import Table from "./Table"
var React = require("react")
var d3 = require("d3")

class App extends React.Component {

  // constructor(props) {
  //   super(props)
  // }

    state = {
      data: [],
      screenWidth: window.innerWidth,
      screenHeight: 500,
    }

  componentWillMount() {
    d3.csv(data)
      .then(data => {

        this.setState({
          data
        })

      })
  }

  render() {

    const { data } = this.state

    const harris_data = data.filter((d) => d.endorsee === "Kamala Harris")

    const columns = data.columns

    return (
      <div className="App">
      <hr
        style={{
            color: "grey",
        }}
    />
      <h1> Kamala Harris </h1>
      <p> U.S. senator from California </p>
      <Table data = { harris_data } columns = { columns } />
      </div>
    );
  }
}

export default App;
