import gridData from "./endorsements_analysed.csv"
var React = require("react")
var d3 = require("d3")

class GridMap extends React.Component {

  state = {
    gridData: [],
  }

  componentWillMount() {
    d3.csv(gridData)
      .then(gridData => {

        gridData.forEach(d =>
          d.points = +d.points
          )

        this.setState({
          gridData
        })

      })
  }

  render() {
    const width = 45
    const height = 45

    const { gridData } = this.state

    var rectScale = d3.scaleLinear()
      .domain(d3.extent(gridData, d =>  d.points))
      .range([1, 30])

    console.log(gridData)
    const gridmap = gridData.map((d,i) =>
      <g key = {"grid" + i} transform={"translate(" + (d.x * width) + "," + (d.y * height) + ")"}>
        <rect
          width = {rectScale(width)}
          height = { rectScale(height) }
          style = {{
            fill : "white",
            stroke: "lightgrey"
          }}
        />
        <text
          textAnchor="middle"
          x = {width/2}
          y = {height/2}
        > {d.state} </text>
      </g>

      )

    return (
      <svg width = {12 * width} height = {8 * height}>
      <g>
      { gridmap }
      </g>
      </svg>
      )
  }
}

export default GridMap
