import gridData from "./endorsements_analysed.csv"
var React = require("react")
var d3 = require("d3")

class GridMap extends React.Component {

  componentWillMount() {
    d3.csv(gridData)
      .then(data => {

        this.setState({
          gridData
        })

      })
  }

  render() {
    const width = 45
    const height = 45
        console.log(this.state)


    const { gridData } = this.state

    console.log(gridData)
    const gridmap = gridData.map((d,i) =>
      <g key = {"grid" + i} transform={"translate(" + (d.x * width) + "," + (d.y * height) + ")"}>
        <rect
          width = {width}
          height = { height }
          style = {{
            fill : "white",
            stroke: "lightgrey"
          }}
        />
        <text
          textAnchor="middle"
          x = {width/2}
          y = {height/2}
        > {d.state} </text>
      </g>

      )

    return (
      <svg width = {12 * width} height = {8 * height}>
      <g>
      { gridmap }
      </g>
      </svg>
      )
  }
}

export default GridMap
