import position from "./position.csv"
import TableBar from "./TableBar"
import { transpose } from "./utils"
var React = require("react")
var d3 = require("d3")


class TableChart extends React.Component {

  state = {
    position: [],
  }

  componentWillMount() {
    d3.csv(position)
      .then(position => {

        this.setState({
          position
        })

      })
  }

  render(){

    if (this.state.position.length > 0) {
      const tableData = this.state.position

      var endorsees = tableData.map(obj => obj.endorsee)

      var stack = d3.stack()
          .keys([
                'Governors',
                'DNC members',
                'Former party leaders',
                'Mayors',
                'Past presidents and vice presidents',
                'Representatives',
                'Senators',
                'State legislative leaders',
                'Statewide officeholders',
              ])
          .order(d3.stackOrderNone)
          .offset(d3.stackOffsetNone)

      var series = stack(tableData)

      var types = series.map(({ key }) => key)

      var colors = d3.scaleOrdinal()
          .domain(types)
          .range(["#d4e8f4", "#bedcee", "#95c6e4", "#82bbe0", "#5fa6d7", "#4692ce"])


     var data = transpose(series)

     // console.log(data)




      // var xScale = d3.scaleLinear()
      //       .domain([0, d3.max(totals_array)])
      //       .range([0, 71]);

    }

    return (
      <table className ="table">
            <tbody>
              {
                data !== undefined ? data.map((rows, i) => (
                <tr key = {"rows" + i}>
                  <td className = "rowBar">{endorsees[i]}</td>
                  <td className = "rowBar">
                    <svg height = {30}>
                      <g className="node" transform="translate(0,65)">
                        {rows.map((d, j) => (
                          <rect
                            key = {j + "rect"}
                            height={20}
                            width={d[1] - d[0]}
                            x={d[0]}
                            style = {{
                              fill: colors(j)
                            }}
                          />
                        ))}
                      </g>
                    </svg>
                  </td>
                </tr>
              )) : null
            }
            </tbody>
          </table>
      )
  }
}

export default TableChart
