import position from "./position.csv"
import TableBar from "./TableBar"
var React = require("react")
var d3 = require("d3")


class TableChart extends React.Component {

  state = {
    position: [],
  }

  componentWillMount() {
    d3.csv(position)
      .then(position => {

        this.setState({
          position
        })

      })
  }

  render(){

    if (this.state.position.length > 0) {
      const tableData = this.state.position

      var endorsee_array = [];
      var totals_array = [];
      tableData.forEach(d => {
        endorsee_array.push(d.endorsee)
        d.totals = (+d["DNC member"]) + (+d["attorney general"]) + (+d["former DNC chair"]) + (+d.governor) + (+d["insurance commissioner"]) + (+d["lieutenant governor"]) + (+d["past vice president"]) + (+d.mayor) + (+d["president"]) + (+d["president pro tem"]) + (+d["representative"]) + (+d["secretary of state"]) + (+d.senator) + (+d.speaker) + (+d["state auditor"]) + (+d["state treasurer"]) + (+d["superintendent of public instruction"])
        totals_array.push(d.totals)
        })

      console.log(totals_array)

      var columns = tableData.columns

      columns.shift()

      var stack = d3.stack()
          .keys(columns)

      var series = stack(tableData)

      var colors = d3.scaleOrdinal()
          .domain(columns)
          .range(["#20363A", "#576F72", "#FF6A28", "#FFD626", "#801F73", "#FAB924"])

          console.log(this.props)

      var xScale = d3.scaleLinear()
            .domain([0, d3.max(totals_array)])
            .range([0, 71]);

      // endorsee_array.forEach(d =>  series[17] = d)


      series.forEach(d => d.endorsee_rows = endorsee_array)

    console.log(series)




      // series.forEach(d =>
      //   console.log(d)
      //   )

      // series.shift();


      // var columns = ["key", "value"]

      // groupedData.length = 10

      // groupedData.sort((a, b) => b.value - a.value );

    }

    return (
      <TableBar rows = {series} endorsee = {endorsee_array} colors= {colors} xScale = {xScale} />
      )
  }
}

export default TableChart
