import position from "./position.csv"
import TableBar from "./TableBar"
var React = require("react")
var d3 = require("d3")


class TableChart extends React.Component {

  state = {
    position: [],
  }

  componentWillMount() {
    d3.csv(position)
      .then(position => {

        this.setState({
          position
        })

      })
  }

  render(){

    console.log(this.state)

    if (this.props.data !== undefined) {
      const tableData = this.props.data

      console.log(tableData)

      // var stack = d3.stack()
      //     .keys(["endorsee", "Worldwide_percent"])


     var groupedData = d3.nest()
      .key(d => d.endorsee)
      .rollup(v =>  d3.sum(v, d => d.points))
      .entries(tableData)

      var columns = ["key", "value"]

      groupedData.length = 10

      groupedData.sort((a, b) => b.value - a.value );

    }

    return (
      <TableBar rows = {groupedData} cols = {columns} />
      )
  }
}

export default TableChart
