import gridData from "./gridmap-layout-usa-538.json"
var React = require("react")
var d3 = require("d3")

class GridMap extends React.Component {
  render() {
    const width = 45
    const height = 45
    console.log(gridData)
    const gridmap = gridData.map((d,i) =>
      <g key = {"grid" + i} transform={"translate(" + (d.x * width) + "," + (d.y * height) + ")"}>
        <rect
          width = {width}
          height = { height }
          style = {{
            fill : "white",
            stroke: "lightgrey"
          }}
        />
        <text
          textAnchor="middle"
          width = {width}
          x = {"2%"}
          height = {height}
        > {d.key} </text>
      </g>

      )

    return (
      <svg width = {12 * width} height = {8 * height}>
      <g>
      { gridmap }
      </g>
      </svg>
      )
  }
}

export default GridMap
