var React = require("react")
var d3 = require("d3")
const _ = require('lodash');

class Table extends React.Component {
  render() {

  if (this.props.columns !== undefined) {
    const tableColumns = this.props.columns
    const tableRows = this.props.data

   const tableColumnsFiltered = tableColumns.filter(d => (d === "date") || (d === "endorser") || (d === "position") || (d === "points"))

   let tableRowsFiltered = _.map(tableRows, d => {
         return _.omit(d, ['city', 'category', "body", "district", "endorsee", "endorser party", "order", "source", "state"])
       })

    console.log(tableRowsFiltered)

     var headers = (
       <thead>
         <tr>
           {
            tableColumnsFiltered.map((d, i) =>
             <th key = {"header" + i}> {d} </th>
             )
          }
         </tr>
       </thead>
       )

     var rows = tableRowsFiltered.map((d, i) =>
         <tr key = {"rows" +i}>
          {
            tableColumns.map((col) =>
              <td> {d[col]} </td>
              )
          }
        </tr>
     )


  }


    return (
      <table className = "table">
        { headers }
        <tbody>
          { rows }
        </tbody>
      </table>
      )
  }
}

export default Table
