import position from "./position.csv"
import TableBar from "./TableBar"
var React = require("react")
var d3 = require("d3")


class TableChart extends React.Component {

  state = {
    position: [],
  }

  componentWillMount() {
    d3.csv(position)
      .then(position => {

        this.setState({
          position
        })

      })
  }

  render(){

    if (this.state.position.length > 0) {
      const tableData = this.state.position

      var endorsee_array = [];
      tableData.forEach(d => endorsee_array.push(d.endorsee))

      var columns = tableData.columns

      columns.shift()

      var stack = d3.stack()
          .keys(columns)

      var series = stack(tableData)

      var colors = d3.scaleOrdinal()
          .domain(columns)
          .range(["#20363A", "#576F72", "#FF6A28", "#FFD626", "#801F73", "#FAB924"])

    var endorsee_sum =  d3.nest()
      .keys(d=> d.endorsee)
      .rollup(v => d3.sum(v, d=> d.points ))
      .entries(tableData)

      console.log(endorsee_sum)

     var totals = endorsee_sum.map(item => item.value).reduce((prev, next) => prev + next);

     console.log(totals)

      var xScale = d3.scale.linear()
            .domain([0, d3.max(totals)])
            .range([0, this.props.height]);

      // series.forEach(d =>
      //   console.log(d)
      //   )

      // series.shift();


      // var columns = ["key", "value"]

      // groupedData.length = 10

      // groupedData.sort((a, b) => b.value - a.value );

    }

    return (
      <TableBar rows = {series} endorsee = {endorsee_array} colors= {colors} />
      )
  }
}

export default TableChart
